    // create arraylist for employee names
    ArrayList<String> names = new ArrayList<String>();
    // create arraylist of arraylists for data
    ArrayList<ArrayList<Integer>> points = new ArrayList<ArrayList<Integer>>();
    // counter
    int count = 0;   
    private void addActionPerformed(java.awt.event.ActionEvent evt) {                                    
        // TODO add your handling code here:
        
        // get all inputs and store in variable
        int week1 = Integer.parseInt (week1in.getText ());
        int week2 = Integer.parseInt (week2in.getText ());
        int week3 = Integer.parseInt (week3in.getText ());
        int week4 = Integer.parseInt (week4in.getText ());
        String fname = fnamein.getText ();
        String lname = lnamein.getText ();
        
        names.add (fname +" "+ lname);
        points.add (new ArrayList<Integer>());
        points.get (count).add (week1);
        points.get (count).add (week2);
        points.get (count).add (week3);
        points.get (count).add (week4);
        count++;
        output.setText ("Points have been added to "+fname+"'s account.");
        // reset
        week1in.setText ("");
        week2in.setText ("");
        fnamein.setText ("");
        lnamein.setText ("");
        week3in.setText ("");
        week4in.setText ("");
    }                                   

