    private void totalPointsActionPerformed(java.awt.event.ActionEvent evt) {                                            
        // TODO add your handling code here:
        String firstname = fnamein.getText ();
        String lastname = lnamein.getText ();
        String fullname = firstname + " " + lastname;
        int sum = 0, points1 = 0, points2 = 0, points3 = 0, points4 = 0, bonus = 0;
        
        for (int i = 0; i < names.size (); i++)
        {
          if (names.get (i).equals (fullname)) // check if in the arraylist
          {
             points1 = points.get (i).get (0);
             points2 = points.get (i).get (1);
             points3 = points.get (i).get (2);
             points4 = points.get (i).get (3);
             sum = points1 + points2 + points3 + points4; // add up all points
             if (sum > 5000)
             {
               bonus = 1000;
             }
             output.setText (fullname+"'s Total points are: "+sum+" Bonus: "+bonus);
             break;
          }
          else
          {
            output.setText ("The name you have entered is not in our databases.");
          }
        }
    }                                           

